$(document).ready(function(){$("#submit_btn").click(function(){var d=$("input[name=name]").val();var a=$("input[name=email]").val();var b=$("textarea[name=message]").val();var c=true;if(d==""){$("input[name=name]").css("border-color","#e41919");c=false}if(a==""){$("input[name=email]").css("border-color","#e41919");c=false}if(b==""){$("textarea[name=message]").css("border-color","#e41919");c=false}if(c){post_data={userName:d,userEmail:a,userMessage:b};$.post("contact/submitMessage",post_data,function(e){if(e.type=="error"){output='<div class="error">'+e.text+"</div>"}else{output='<div class="success">'+e.text+"</div>";$("#contact_form input").val("");$("#contact_form textarea").val("")}$("#result").hide().html(output).slideDown()},"json")}return false});$("#contact_form input, #contact_form textarea").keyup(function(){$("#contact_form input, #contact_form textarea").css("border-color","");$("#result").slideUp()})});